        }
    }, [pageHighlight])

    return (
        <HStack
            p={'4'}
            shadow={'base'}
            bgColor={'blackAlpha.900'}
            position={'sticky'}
            top={'0'}
            left={'0'}
            zIndex={'overlay'}
            justifyContent={['space-between', 'flex-start']}
        >
            <Button _hover={[{ transform: 'scale(1)' }, { transform: 'scale(1.1)' }]} px={[0, 15]} fontSize={[15, 25]} variant={'unstyled'} color={'white'} bgColor={pageHighlight === 'home' ? 'white' : 'whiteAlpha.100'} onClick={() => { setPageHighlight("home") }}>
                <Link to={'/'}>Home</Link>
            </Button>
            <Button _hover={[{ transform: 'scale(1)' }, { transform: 'scale(1.1)' }]} px={[0, 15]} fontSize={[15, 25]} variant={'unstyled'} color={'white'} bgColor={pageHighlight === 'exchanges' ? 'white' : 'whiteAlpha.100'} onClick={() => { setPageHighlight("exchanges") }}>
                <Link to={'/exchanges'}>Exchanges</Link>
            </Button>
            <Button _hover={[{ transform: 'scale(1)' }, { transform: 'scale(1.1)' }]} px={[0, 15]} fontSize={[15, 25]} variant={'unstyled'} color={'white'} bgColor={pageHighlight === 'coins' ? 'white' : 'whiteAlpha.100'} onClick={() => { setPageHighlight("coins") }} textTransform={'capitalize'}>
                <Link to={'/coins'} >coins</Link>
            </Button>
        </HStack>
    )
}

export default Header;
