import { Box, Container, HStack, Image, Radio, RadioGroup, Text, VStack } from '@chakra-ui/react';
import React, { useEffect, useState } from 'react';
import Loader from '../components/Loader.jsx';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import { server } from '../index.js';
import ErrorComponent from './ErrorComponent.jsx';
const CoinDetails = () => {

    const [coin, setCoin] = useState({});
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(false);
    const [currency, setCurrency] = useState('inr');
    const params = useParams();
    useEffect(() => {
        const fetchCoin = async () => {
            try {
                const { data } = await axios.get(`${server}/coins/${params.id}`);
                setCoin(data);
                console.log(data);
            } catch (error) {
                setError(true);
            }
            setLoading(false);
        };
        fetchCoin();
    }, [currency, loading])
    if (error)
        return (<ErrorComponent message={"Error while Fetching Coin Details"} />)

    return (

        <Container
            maxW={'container.xl'}
        >
            {loading ? <Loader /> : <>
                <Box
                    borderWidth={'1'}
                    width={'full'}
                >
                </Box>
                <RadioGroup value={currency} onChange={setCurrency} p={'8'}>
                    <HStack spacing={'4'} >
                        <Radio value={'inr'} >INR</Radio>
                        <Radio value={'usd'} >USD</Radio>
                        <Radio value={'eur'} >EUR</Radio>
                    </HStack>
                </RadioGroup>
                <VStack
                    spacing={'4'}
                    p={'16'}
                    alignItems={'flex-start'}

                >
                    <Text
                        fontSize={'sm'}
                        alignSelf={'center'}
                        opacity={'0.7'}
                    >
                        Last Updated on {Date().split('G')[0]}
                    </Text>
                    <Image src={coin.image.large} />
                </VStack>


            </>}
        </Container>
    )
}

export default CoinDetails
