import { HStack, Button } from '@chakra-ui/react';
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
const Header = () => {
    // const hlink = window.location.href.split('.vercel.app');
    const hlink = window.location.href.split('host:3000')[1].split('/')[0];
    const pageHighlight = hlink;
    // setPageHighlight(hlink);
    console.log(hlink)

    return (
        <HStack
            p={'4'}
            shadow={'base'}
            bgColor={'blackAlpha.900'}
            position={'sticky'}
            top={'0'}
            left={'0'}
            zIndex={'overlay'}
            justifyContent={['space-between', 'flex-start']}
        >
            <Button _hover={[{ transform: 'scale(1)' }, { transform: 'scale(1.1)' }]} px={[0, 15]} fontSize={[15, 25]} variant={'unstyled'} color={pageHighlight === '/' ? 'white' : 'whiteAlpha.700'} >
                <Link to={'/'}>Home</Link>
            </Button>
            <Button _hover={[{ transform: 'scale(1)' }, { transform: 'scale(1.1)' }]} px={[0, 15]} fontSize={[15, 25]} variant={'unstyled'} color={pageHighlight === '/exchanges' ? 'white' : 'whiteAlpha.700'} >
                <Link to={'/exchanges'}>Exchanges</Link>
            </Button>
            <Button _hover={[{ transform: 'scale(1)' }, { transform: 'scale(1.1)' }]} px={[0, 15]} fontSize={[15, 25]} variant={'unstyled'} color={pageHighlight === 'coins' ? 'white' : 'whiteAlpha.700'} textTransform={'capitalize'}>
                <Link to={'/coins'} >coins</Link>
            </Button>
        </HStack>
    )
}

export default Header;
