import { Container, Spinner } from '@chakra-ui/react'
import React from 'react'

const Loader = () => {
    return (
        <Container
            display={'flex'}
            justifyContent={'center'}
            alignItems={'center'}
            h={'80vh'}
        >
            <Spinner size={'500'}></Spinner>
        </Container>
    )
}

export default Loader
