import { Container, Box, Center } from '@chakra-ui/react'
import React from 'react'

const Loader = () => {
    return (
        <Container
            display={'flex'}
            justifyContent={'center'}
            alignItems={'center'}
            h={'80vh'}
        >
            <Box
                h={['20vw', '40vw']}
                w={['20vw', '40vw']}
                borderBottom={'2px solid black'}
                transform={'rotateX(360eg)'}
                transition={'all 2000'}
            >
            </Box>
        </Container>
    )
}

export default Loader
